w_tickethistory" AS 
select o.idocorrencia as id, o.idsolicitacaoservico as request_id, o.iditemtrabalho as workitem_id, e.nome as workitem, 
o.dataregistro as date, o.horaregistro as hour, 
case 
when lower(o.registradopor) = 'automatic' or lower(o.registradopor) = 'automático' then 'bpm.enum.assignmentType.automatic'
when u.nome is null then o.registradopor 
else u.nome 
end as responsible,
idoccurrencecategory as type_id, 
case 
when idoccurrencecategory = 1 then 'citcorpore.comum.registroDaSolicitacao'
when idoccurrencecategory = 2 then 'citcorpore.comum.acompanhamentoCliente'
when idoccurrencecategory = 3 then 'citcorpore.comum.atualizacaoStatus'
when idoccurrencecategory = 4 then 'problema.diagnostico'
when idoccurrencecategory = 5 then 'citcorpore.comum.investigacao'
when idoccurrencecategory = 6 then 'citcorpore.comum.memorando'
when idoccurrencecategory = 7 then 'citcorpore.comum.pedidoInformacao'
when idoccurrencecategory = 8 then 'citcorpore.comum.retornoCliente'
when idoccurrencecategory = 9 then 'citcorpore.comum.sintomaProblema'
when idoccurrencecategory = 10 then 'problema.solucao_contorno'
when idoccurrencecategory = 11 then 'solicitacaoServico.registroExecucao'
when idoccurrencecategory = 12 then 'citcorpore.comum.mudançaSLA'
when idoccurrencecategory = 13 then 'citcorpore.comum.reclassificacao'
when idoccurrencecategory = 14 then 'citcorpore.comum.agendamentoAtividade'
when idoccurrencecategory = 15 then 'citcorpore.comum.suspensaoSolicitacao'
when idoccurrencecategory = 16 then 'citcorpore.comum.reativacaoSolicitacao'
when idoccurrencecategory = 17 then 'citcorpore.comum.encerramentoSolicitacao'
when idoccurrencecategory = 18 then 'citcorpore.comum.reaberturaSolicitacao'
when idoccurrencecategory = 19 then 'citcorpore.comum.direcionamentoSolicitacao'
when idoccurrencecategory = 20 then 'citcorpore.comum.compartilhamentoTarefa'
when idoccurrencecategory = 21 then 'citcorpore.comum.cancelamentoTarefa'
when idoccurrencecategory = 22 then 'citcorpore.comum.inicioSLA'
when idoccurrencecategory = 23 then 'citcorpore.comum.suspensaoSLA'
when idoccurrencecategory = 24 then 'citcorpore.comum.liberacaoRequisicaoLiberacao'
when idoccurrencecategory = 25 then 'citcorpore.comum.reativacaoSLA'
when idoccurrencecategory = 26 then 'citcorpore.comum.outrasCategorias'
when idoccurrencecategory = 27 then 'baseConhecimentoView.confidencial'
when idoccurrencecategory = 27 then 'citcorpore.comum.localizacao'
when idoccurrencecategory = 28 then 'citcorpore.comum.respostaAutomatica'
when idoccurrencecategory is null then 'solicitacaoServico.registroExecucao'
end as type,
case when o.descricao like '<%' then '' else o.descricao end as description ,
replace(replace(o.ocorrencia,'<p>',''),'</p>','') as occurrency
from ocorrenciasolicitacao o left join (select distinct login, min(nome) as nome from usuario group by login) u
on u.login = o.registradopor
left join bpm_itemtrabalhofluxo i on i.iditemtrabalho = o.iditemtrabalho 
