var $ticketScope = angular.element("#service-request-view").scope();

var findOccurrenceNotes = function() {
	JANELA_AGUARDE_MENU.show();
	ServiceRequestIncidentRepository.findOccurrenceNotes({id: $rootScope.request.id, idItemTrabalho: $rootScope.idWorkItem}).then(function(result) {
		JANELA_AGUARDE_MENU.hide();
		updateOccurrencesTimestamp(result.data, 'occurrenceNotes');
	});
};

var updateOccurrencesTimestamp = function(occurrences, attribute) {
 TicketOccurrenceRepository.listTimestampByTicket({idSolicitacaoServico: $rootScope.request.id}).then(function(result) {
 var i = 0;
		angular.forEach(occurrences, function(occurrence) {
			angular.forEach(result, function(occurrenceWithTimestamp) {
			 if (occurrence.idOcorrencia == occurrenceWithTimestamp.idOcorrencia) {
			 occurrences[i].startTime = $filter('date')(moment(occurrenceWithTimestamp.startTime).toDate(), $scope.datetimeFormat);
			 occurrences[i].endTime = $filter('date')(moment(occurrenceWithTimestamp.endTime).toDate(), $scope.datetimeFormat);
			 }
			}); 
			i++;
		}); 
		$timeout(function(){
			$ticketScope[attribute] = occurrences;
		},10);
 });
};

var initOccurrenceNote = function() {
	$ticketScope.occurrenceNote = {
		date: new Date(),
		startTime: $filter('date')(new Date(), $scope.datetimeFormat),
		endTime: $filter('date')(new Date(), $scope.datetimeFormat)
	};
};

var updateOccurrenceNote = function(occurrenceTimesheet) {
 JANELA_AGUARDE_MENU.show();
	ServiceRequestIncidentRepository.findOccurrenceNotes({id: $rootScope.request.id, idItemTrabalho: $rootScope.idWorkItem}).then(function(result) {
		JANELA_AGUARDE_MENU.hide();
		
		var occurrences = result.data;
		var lastOccurrence = null;
		angular.forEach(occurrences, function(occurrence) {
		 if (occurrence.ocorrencia == occurrenceTimesheet.description 
		 && occurrence.tempoGastoData.substring(0,10) == occurrenceTimesheet.startTime.substring(0,10)
		 && occurrence.tempoGastoHora == occurrenceTimesheet.time) {
 lastOccurrence = occurrence;			 
		 }
		});
		
		if (lastOccurrence) {
		 lastOccurrence.startTime = occurrenceTimesheet.startTime;
		 lastOccurrence.endTime = occurrenceTimesheet.endTime;
		 
		 TicketOccurrenceRepository.updateTimestamp(lastOccurrence).then(function(result) {
				updateOccurrencesTimestamp(occurrences, 'occurrenceNotes');
				findOccurrences();
 			initOccurrenceNote();
		 });
		}else{
			initOccurrenceNote();
		}
	});
};

var saveOccurrenceTimesheet = function(occurrenceTimesheet) {
	var occurrence = {
		idSolicitacaoServico: $rootScope.request.id,
		idItemTrabalho: $rootScope.request.idItemTrabalho,
		tempoGastoData: occurrenceTimesheet.date,
		tempoGastoHora: occurrenceTimesheet.time,
		ocorrencia: occurrenceTimesheet.description,
		notificarSolicitante: (occurrenceTimesheet.notify ? 'S' : 'N'),
		visivelSolicitante: (occurrenceTimesheet.visibleToRequester ? 'S' : 'N'),
		view: false
	};
	
	JANELA_AGUARDE_MENU.show();
	return ServiceRequestIncidentRepository.saveOccurrenceTimesheet(occurrence).then(function(result) {
		JANELA_AGUARDE_MENU.hide();
		
		Notification.success($translate.instant('citcorpore.comum.adicionadoComSucesso', {
			msg1: $translate.instant('citcorpore.comum.comentario')
		}));

		updateOccurrenceNote(occurrenceTimesheet);
	});
};

$ticketScope.openServiceRequestIncidentAddOccurrenceNote = function() {
	$ticketScope.setFormNewNoteCollapsed(isMobile());
	$ticketScope.isServiceRequestIncidentAddOccurrenceNoteOpened = true;
	
	initOccurrenceNote();
	
	FormUtil.focusOnField('[id="occurrenceNote.description"]');
};

$ticketScope.customSaveOccurrenceNoteValidation = function(form, occurrenceNote) {
 if(!occurrenceNote.startTime && !occurrenceNote.endTime) {
 $ticketScope.saveOccurrenceNoteValidation(form, occurrenceNote);
 return;
 }
 
	if(!FormUtil.valid(form, false)) {
		FormUtil.focusOnField('[id="occurrenceNote.description"]');
		return ;
	}
	
	if(!occurrenceNote.startTime || !occurrenceNote.endTime) {
		Notification.error('audit.msg.dtInicioDtFimPreenchidas');
		return ;
	}
	
	if(moment(occurrenceNote.startTime).isAfter(moment(occurrenceNote.endTime))) {
	 Notification.error('audit.msg.dtInicial.dtFinal.menor');
	 return;
	}
	 
 FormUtil.toInitialMode(form);
	$ticketScope.setFormNewNoteCollapsed(true);
	
	occurrenceNote.date = $filter('date')(moment(occurrenceNote.startTime).toDate(), 'yyyy-MM-dd');
	occurrenceNote.time = moment(occurrenceNote.endTime).toDate() - moment(occurrenceNote.startTime).toDate();
